if doc.status == "Approved":
    if doc.customer_email:
        email_template = frappe.get_doc("Email Template", "Booking Approved Template")
        context = doc.as_dict()
        subject = frappe.render_template(email_template.subject, context)
        message = frappe.render_template(email_template.response_html, context)
        frappe.sendmail(
            recipients="bhandarigreecy@gmail.com",
            subject=subject,
            message=message,
        )
    else:
        frappe.msgprint(f"No contact email found for customer {doc.customer}")
